	{
		#region Methods

		[TestMethod]
		public void CharactersToString()
		{
			Assert.AreEqual("a", new KeyboardState { Key = KeyboardKey.A }.ToString());
			Assert.AreEqual("b", new KeyboardState { Key = KeyboardKey.B }.ToString());
			Assert.AreEqual("c", new KeyboardState { Key = KeyboardKey.C }.ToString());
			Assert.AreEqual("d", new KeyboardState { Key = KeyboardKey.D }.ToString());

			Assert.AreEqual("A", new KeyboardState { Key = KeyboardKey.A, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("A", new KeyboardState { Key = KeyboardKey.A, IsRightShiftPressed = true }.ToString());
			Assert.AreEqual("A", new KeyboardState { Key = KeyboardKey.A, IsCapsLockOn = true }.ToString());
			Assert.AreEqual("B", new KeyboardState { Key = KeyboardKey.B, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("B", new KeyboardState { Key = KeyboardKey.B, IsRightShiftPressed = true }.ToString());
			Assert.AreEqual("B", new KeyboardState { Key = KeyboardKey.B, IsCapsLockOn = true }.ToString());
		}

		[TestMethod]
		public void ConvertCharToKeyboardState()
		{
			var scenarios = GetScenarios();

			foreach (var scenario in scenarios)
			{
				var actual = KeyboardState.FromCharacter(scenario.Character);
				actual.IsLeftShiftPressed = scenario.IsLeftShiftPressed;
				actual.Key = scenario.Key;

				if (!scenario.Equals(actual))
				{
					GenerateScenarios();
					Assert.Fail("boom");
				}
			}
		}

		[TestMethod]
		public void ConvertKeyboardStateToString()
		{
			var scenarios = GetScenarios();

			foreach (var scenario in scenarios)
			{
				if (scenario.Character != Keyboard.ToCharacter(scenario.Key, scenario))
				{
					scenario.ToDetailedString().Dump();
					GenerateScenarios();
					Assert.Fail("boom");
				}
			}
		}

		[TestMethod]
		public void GenerateScenarios()
		{
			var builder = new StringBuilder();
			builder.AppendLine("return new[]");
			builder.AppendLine("{");

			foreach (var key in Enum.GetValues(typeof(KeyboardKey)).Cast<KeyboardKey>())
			{
				var expected = new KeyboardState { Key = key };
				AddState(builder, expected);
				expected.IsLeftShiftPressed = true;
				AddState(builder, expected);
			}

			builder.Append("};");
			var actual = builder.ToString();
			actual.Dump();

			var test = new Clipboard();
			test.SetText(actual);
		}

		[TestMethod]
		public void NumbersToString()
		{
			Assert.AreEqual("0", new KeyboardState { Key = KeyboardKey.Number0 }.ToString());
			Assert.AreEqual("1", new KeyboardState { Key = KeyboardKey.Number1 }.ToString());
			Assert.AreEqual("2", new KeyboardState { Key = KeyboardKey.Number2 }.ToString());
			Assert.AreEqual("3", new KeyboardState { Key = KeyboardKey.Number3 }.ToString());
			Assert.AreEqual("4", new KeyboardState { Key = KeyboardKey.Number4 }.ToString());
			Assert.AreEqual("5", new KeyboardState { Key = KeyboardKey.Number5 }.ToString());
			Assert.AreEqual("6", new KeyboardState { Key = KeyboardKey.Number6 }.ToString());
			Assert.AreEqual("7", new KeyboardState { Key = KeyboardKey.Number7 }.ToString());
			Assert.AreEqual("8", new KeyboardState { Key = KeyboardKey.Number8 }.ToString());
			Assert.AreEqual("9", new KeyboardState { Key = KeyboardKey.Number9 }.ToString());

			Assert.AreEqual("!", new KeyboardState { Key = KeyboardKey.Number1, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("@", new KeyboardState { Key = KeyboardKey.Number2, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("#", new KeyboardState { Key = KeyboardKey.Number3, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("$", new KeyboardState { Key = KeyboardKey.Number4, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("%", new KeyboardState { Key = KeyboardKey.Number5, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("^", new KeyboardState { Key = KeyboardKey.Number6, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("&", new KeyboardState { Key = KeyboardKey.Number7, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("*", new KeyboardState { Key = KeyboardKey.Number8, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual("(", new KeyboardState { Key = KeyboardKey.Number9, IsLeftShiftPressed = true }.ToString());
			Assert.AreEqual(")", new KeyboardState { Key = KeyboardKey.Number0, IsLeftShiftPressed = true }.ToString());
		}

		private static void AddState(StringBuilder builder, KeyboardState expected)
		{
			var properties = typeof(KeyboardState).GetCachedProperties();
			expected.Character = Keyboard.ToCharacter(expected.Key, expected);
			builder.Append("new KeyboardState {");

			foreach (var p in properties)
			{
				if (!p.CanWrite)
				{
					continue;
				}

				if (p.PropertyType == typeof(bool))
				{
					builder.Append($" {p.Name} = {p.GetValue(expected).ToString().ToLower()},");
				}
				else if (p.PropertyType == typeof(char?))
				{
					var c = (char?) p.GetValue(expected);
					if ((c >= 32) && (c <= 126))
					{
						if ((c == 39) || (c == 92))
						{
							builder.Append($" {p.Name} = '\\{c}',");
						}
						else
						{
							builder.Append($" {p.Name} = '{c}',");
						}
					}
					else if (c == null)
					{
						builder.Append($" {p.Name} = null,");
					}
					else
					{
						builder.Append($" {p.Name} = '\\u{(int) c:X4}',");
					}
				}
				else if (p.PropertyType == typeof(KeyboardKey))
				{
					builder.Append($" {p.Name} = KeyboardKey.{p.GetValue(expected)},");
				}
				else
				{
					builder.Append($" {p.Name} = {p.GetValue(expected)},");
				}
			}

			builder.Remove(builder.Length - 1, 1);
			builder.AppendLine(" },");
		}

